import React, { useState, useEffect, useRef } from 'react';
import SpotifyWebApi from 'spotify-web-api-node';

function Playlist({ accessToken }) {
    const [userID, setUserID] = useState();
    const [playlist, setPlaylist] = useState(null); // New state variable for the playlist
    const [newPlaylist, setNewPlaylist] = useState(null);
    const [playlistDescription, setPlaylistDescription] = useState('');

    const spotifyApi = useRef(
        new SpotifyWebApi({
            clientId: "6ba0cc8b29e145ea99f2401c09a35e6e",
        })
    );

    useEffect((playlist) => {
        console.log('Playlist state:', playlist);
    }, [playlist]);



    useEffect(() => {
        if (!accessToken) return;
        spotifyApi.current.setAccessToken(accessToken);

        // Get the user's ID
        spotifyApi.current.getMe().then((res) => {
            setUserID(res.body.id);
            console.log('User ID:', res.body.id); // Log the user ID
        });
    }, [accessToken]);



    // Cant use callbacks here, playlist stays undefined
    const createPlaylist = async () => {
        try {
            // Get the user's top tracks
            const topTracksResponse = await spotifyApi.current.getMyTopTracks({ limit: 5 });
            const trackIds = topTracksResponse.body.items.map(track => track.id);

            // Get recommendations based on these tracks
            const recommendationsResponse = await spotifyApi.current.getRecommendations({ seed_tracks: trackIds });
            const trackUris = recommendationsResponse.body.tracks.map(track => track.uri);

            // Create a new playlist in the state
            setNewPlaylist({ name: 'New Playlist', tracks: trackUris });
        } catch (err) {
            console.error('Error:', err);
        }
    };

    const savePlaylist = async () => {
        try {
            // Create a new playlist on Spotify
            const playlistData = await spotifyApi.current.createPlaylist(userID, newPlaylist.name, { 'public' : false, 'description': playlistDescription });
            const playlistId = playlistData.body.id;

            // Add tracks to the playlist
            await spotifyApi.current.addTracksToPlaylist(playlistId, newPlaylist.tracks);

            // Fetch the playlist data
            const data = await spotifyApi.current.getPlaylist(playlistId);
            setPlaylist(data.body); // Update the playlist state
        } catch (err) {
            console.error('Error:', err);
        }
    };



    return (
        <div className="playlist">
        {/* ... */}
        <button onClick={createPlaylist}>Create</button>
        {newPlaylist ? (
            <div>
                <h2>{newPlaylist.name}</h2>
                {/* Render the new playlist tracks */}
                {newPlaylist.tracks && newPlaylist.tracks.length > 0 ? (
                    newPlaylist.tracks.map((trackUri, index) => (
                        <div key={index}>
                            <p>{trackUri}</p>
                        </div>
                    ))
                ) : (
                    <p>No tracks in playlist.</p>
                )}
                <input type="text" placeholder="Enter a description" onChange={e => setPlaylistDescription(e.target.value)} />
                <button onClick={savePlaylist}>Save to Spotify</button>
            </div>
        ) : (
            <p>No new playlist data to display.</p>
        )}
        {/* ... */}
    </div>
);

}

export default Playlist;
