import React from "react";
import useAuth from "../../Auth/useAuth";
import { useState, useEffect, useRef } from "react";
import "./Dashboard.scss";
import Header from "../Header/Header";
import Tracks from "../Tracks/Tracks";
import Artists from "../Artists/Artists"; // import Artists
import Genres from "../Genres/Genres"; // import Genres
import RecentlyPlayed from "../RecentlyPlayed/RecentlyPlayed"; // import RecentlyPlayed

function Dashboard({ code, spotifyApi }) {
  const accessToken = useAuth(code);
  const [selectedTimeRange, setSelectedTimeRange] = useState("short_term");
  const [selectedCategory, setSelectedCategory] = useState(null);

  useEffect(() => {
    if (!accessToken) return;
    spotifyApi.current.setAccessToken(accessToken);
  });

  return (
    <div className="dashboard">
      <Header onCategorySelect={setSelectedCategory} />
      {selectedCategory && selectedCategory !== "recently-played" && (
        <section className="dashboard__hero">
          <button
            className="dashboard__btn"
            onClick={() => setSelectedTimeRange("short_term")}
          >
            Past 4 weeks
          </button>
          <button
            className="dashboard__btn"
            onClick={() => setSelectedTimeRange("medium_term")}
          >
            Past 6 months
          </button>
          <button
            className="dashboard__btn"
            onClick={() => setSelectedTimeRange("long_term")}
          >
            All time
          </button>
        </section>
      )}
      <div className="dashboard__top">
        <Tracks selectedTimeRange={selectedTimeRange} />
        <Artists selectedTimeRange={selectedTimeRange} />{" "}
        {/* pass selectedTimeRange to Artists */}
        <Genres selectedTimeRange={selectedTimeRange} />{" "}
        {/* pass selectedTimeRange to Genres */}
        <RecentlyPlayed selectedTimeRange={selectedTimeRange} />{" "}
        {/* pass selectedTimeRange to RecentlyPlayed */}
      </div>
    </div>
  );
}

export default Dashboard;
