import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import SpotifyWebApi from "spotify-web-api-node";

function Mood({ accessToken }) {
    const ref = useRef();
    const spotifyApi = useRef(
        new SpotifyWebApi({
            clientId: "6ba0cc8b29e145ea99f2401c09a35e6e",
        })
    );

    useEffect(() => {
        spotifyApi.current.setAccessToken(accessToken);

        // Fetch the user's listening history
        spotifyApi.current.getMyRecentlyPlayedTracks({ limit: 50 })
            .then(response => {
                const tracks = response.body.items;

                // Get the audio features for these tracks
                return spotifyApi.current.getAudioFeaturesForTracks(tracks.map(track => track.track.id));
            })
            .then(response => {
                const audioFeatures = response.body.audio_features;

                // Store the mood data in a time-series format
                const moodData = audioFeatures.map((features, index) => ({
                    date: new Date().getTime() - index * 1000 * 60 * 60 * 24, // This is a placeholder. You should use the actual play time of each track.
                    valence: features.valence
                }));

                // Map the valence to a mood category
                const moodCategory = valence => valence < 0.33 ? 'Sad' : valence < 0.66 ? 'Neutral' : 'Happy';
                moodData.forEach(d => {
                    d.mood = moodCategory(d.valence);
                });

                // Create the line chart with D3.js
                const svg = d3.select(ref.current);
                const margin = {top: 20, right: 20, bottom: 50, left: 50};
                const width = 960 - margin.left - margin.right;
                const height = 500 - margin.top - margin.bottom;

                const xScale = d3.scaleTime().domain(d3.extent(moodData, d => d.date)).range([0, width]);
                const yScale = d3.scaleLinear().domain([0, 1]).range([height, 0]);

                const line = d3.line().x(d => xScale(d.date)).y(d => yScale(d.valence));

                const g = svg.append('g').attr('transform', 'translate(' + margin.left + ',' + margin.top + ')');

                g.append('g')
                    .attr('transform', 'translate(0,' + height + ')')
                    .call(d3.axisBottom(xScale))
                    .append('text')
                    .attr('fill', '#fff')
                    .attr('y', 20)
                    .attr('x', width / 2)
                    .attr('dy', '0.71em')
                    .attr('text-anchor', 'end')
                    .text('Time');

                g.append('g')
                    .call(d3.axisLeft(yScale))
                    .append('text')
                    .attr('fill', '#fff')
                    .attr('transform', 'rotate(-90)')
                    .attr('y', -40)
                    .attr('dy', '0.71em')
                    .attr('text-anchor', 'end')
                    .text('Mood (valence)');

                g.append('path')
                    .datum(moodData)
                    .attr('fill', 'none')
                    .attr('stroke', 'steelblue')
                    .attr('stroke-width', 1.5)
                    .attr('d', line);

                svg.append('text')
                    .attr('x', (width / 2))             
                    .attr('y', 20)
                    .attr('text-anchor', 'middle')  
                    .style('font-size', '20px') 
                    .style('text-decoration', 'underline')  
                    .style('fill', '#fff')
                    .text('Mood Over Time');

                // Add mood labels
                g.selectAll('.mood-label')
                    .data(moodData)
                    .enter()
                    .append('text')
                    .attr('class', 'mood-label')
                    .attr('x', d => xScale(d.date))
                    .attr('y', d => yScale(d.valence))
                    .attr('dy', '-1em')
                    .attr('fill', '#fff')
                    .text(d => d.mood);
            })
            .catch(err => {
                console.error('Something went wrong:', err);
            });
    }, [accessToken]);

    return <svg ref={ref} width="960" height="500" />;
}

export default Mood;
