import React, { useState, useEffect } from 'react';
import SpotifyWebApi from 'spotify-web-api-node';

function Playlist({ accessToken }) {
    const [userID, setUserID] = useState();
    const spotifyApi = new SpotifyWebApi();

    useEffect(() => {
        if (!accessToken) return;
        spotifyApi.setAccessToken(accessToken);

        // Get the user's ID
        spotifyApi.getMe().then((res) => {
            setUserID(res.body.id);
        });
    }, [accessToken]);

    const createPlaylist = async () => {
        // Get the user's top tracks
        const topTracksResponse = await spotifyApi.getMyTopTracks({ limit: 5 });
        const trackIds = topTracksResponse.body.items.map(track => track.id);

        // Get recommendations based on these tracks
        const recommendationsResponse = await spotifyApi.getRecommendations({ seed_tracks: trackIds });
        const trackUris = recommendationsResponse.body.tracks.map(track => track.uri);

        // Create a new playlist
        const playlistResponse = await spotifyApi.createPlaylist(userID, 'New Playlist', { 'public' : false });
        const playlistId = playlistResponse.body.id;

        // Add the recommended tracks to the playlist
        await spotifyApi.addTracksToPlaylist(playlistId, trackUris);

        console.log('Created playlist!');
    };

    return (
        <div className="dashboard__playlist">
            <button onClick={createPlaylist}>Create Playlist</button>
        </div>
    );
}

export default Playlist;
