import React, { useState, useEffect, useRef } from "react";
import useAuth from "../../Auth/useAuth";
import SpotifyWebApi from "spotify-web-api-node";
import SpotifyIcon from "../../assets/images/Spotify_Icon_RGB_White.png";

function Tracks({ code }) {
  const accessToken = useAuth(code);
  const [topTracks, setTopTracks] = useState([]);
  const [selectedTimeRange, setSelectedTimeRange] = useState("short_term");

  const spotifyApi = useRef(
    new SpotifyWebApi({
      clientId: "6ba0cc8b29e145ea99f2401c09a35e6e",
    })
  );

  useEffect(() => {
    if (!accessToken) return;
    spotifyApi.current.setAccessToken(accessToken);

    spotifyApi.current.getMyTopTracks({ time_range: selectedTimeRange, limit: 50 }).then((res) => {
      setTopTracks(res.body.items);
    });
  }, [accessToken, selectedTimeRange]);

  return (
    <div className="dashboard__tracks">
      <button onClick={() => setSelectedTimeRange("short_term")}>Short Term</button>
      <button onClick={() => setSelectedTimeRange("medium_term")}>Medium Term</button>
      <button onClick={() => setSelectedTimeRange("long_term")}>Long Term</button>

      {topTracks.map((track, index) => (
        <div key={index} className="track">
          <div className="track__container">
            <span className="track__rank">{index + 1}</span>
            <img
              src={track.album?.images[0]?.url}
              alt={track.name}
              className="track__image"
            />
            <span className="track__title">{track.name}</span>
          </div>
          <div className="track__container">
            <span className="track__artist">
              {track.artists.map((artist) => artist.name).join(", ")}
            </span>
          </div>
          <div className="track__link">
            <a
              href={track.external_urls.spotify}
              target="_blank"
              rel="noopener noreferrer"
            >
              <img
                src={SpotifyIcon}
                alt="Spotify Icon"
                className="track__icon"
              />
            </a>
          </div>
        </div>
      ))}
    </div>
  );
}

export default Tracks;
