import React, { useState } from 'react';
import useAuth from '../../useAuth';
import './Settings.scss';

function Settings({ accessToken, setAccessToken, setRefreshToken }) {
  const [isOpen, setIsOpen] = useState(false);

  const handleEmailOpt = (e) => {
    // handle email opt in/out
  };

  const handleSpotifyRedirect = () => {
    // redirect to user's Spotify page
    window.location.href = 'https://open.spotify.com';
  };

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  function logout() {
    // Clear the tokens from the application's state
    setAccessToken(null);
    setRefreshToken(null);

    // If you're storing the tokens in a cookie or local storage, clear them from there as well
    localStorage.removeItem('accessToken');
    localStorage.removeItem('refreshToken');

    // Redirect the user to the login page
    window.location = "/";
  }

  return (
    <div className="settings">
      <h2 className="settings__title" onClick={toggleDropdown}>
        Settings &#9662;
      </h2>
      {isOpen && (
        <ul className="settings__dropdown">
          <li onClick={handleEmailOpt}>Toggle Email Opt-In/Out</li>
          <li onClick={handleSpotifyRedirect}>Go to Spotify</li>
          <li onClick={logout}>Logout</li>
        </ul>
      )}
    </div>
  );
}

export default Settings;

