import React, { createContext, useContext, useEffect, useState, useRef } from "react";
import useAuth from "./useAuth";
import SpotifyWebApi from "spotify-web-api-node";

export const AuthContext = createContext();

export function AuthProvider({ children, code }) {
  const accessToken = useAuth(code);
  console.log(accessToken);
  const [refreshToken, setRefreshToken] = useState(localStorage.getItem("refreshToken"));

  const spotifyApi = useRef(new SpotifyWebApi({ clientId: "6ba0cc8b29e145ea99f2401c09a35e6e" }));

  const cancel = useRef(false);
  const debounceTimeoutRef = useRef(null);

  const handleLogout = () => {
    localStorage.removeItem("accessToken");
    localStorage.removeItem("refreshToken");
    setRefreshToken(null);

    window.location.href = "/";
  };

  return (
    <AuthContext.Provider value={{ accessToken, refreshToken, handleLogout, spotifyApi, cancel, debounceTimeoutRef }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuthContext() {
  return useContext(AuthContext);
}
