import React from "react";
import useAuth from "../../useAuth";
import { useState, useEffect, useRef } from "react";
import "./Dashboard.scss";
import spotifyWebApi from "spotify-web-api-node";
import TrackSearchResult from "../TrackSearchResult";
import Player from "../Player";
import Header from "../Header/Header";
import { Routes, Route } from "react-router-dom";

function Dashboard({ code }) {
  const accessToken = useAuth(code);
  const [search, setSearch] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [playingTrack, setPlayingTrack] = useState();
  const [topArtists, setTopArtists] = useState([]);
  const [topTracks, setTopTracks] = useState([]);
  const [topGenres, setTopGenres] = useState([]);
  const [recentlyPlayed, setRecentlyPlayed] = useState([]);
  const [recentlyPlayedTracks, setRecentlyPlayedTracks] = useState([]);
  const [topArtistsShort, setTopArtistsShort] = useState([]);
  const [topArtistsMedium, setTopArtistsMedium] = useState([]);
  const [topArtistsLong, setTopArtistsLong] = useState([]);
  const [topTracksShort, setTopTracksShort] = useState([]);
  const [topTracksMedium, setTopTracksMedium] = useState([]);
  const [topTracksLong, setTopTracksLong] = useState([]);
  const [selectedTimeRange, setSelectedTimeRange] = useState("short_term");

  function chooseTrack(track) {
    setPlayingTrack(track);
    setSearch("");
  }

  console.log(searchResults);

  const spotifyApi = useRef(
    new spotifyWebApi({
      clientId: "6ba0cc8b29e145ea99f2401c09a35e6e",
    })
  );
  const cancel = useRef(false);
  const debounceTimeoutRef = useRef(null);

  useEffect(() => {
    if (!accessToken) return;
    spotifyApi.current.setAccessToken(accessToken);

    spotifyApi.current
      .getMyTopArtists({ time_range: "short_term" })
      .then((res) => {
        setTopArtistsShort(res.body.items);
      });
    spotifyApi.current
      .getMyTopArtists({ time_range: "medium_term" })
      .then((res) => {
        setTopArtistsMedium(res.body.items);
      });
    spotifyApi.current
      .getMyTopArtists({ time_range: "long_term" })
      .then((res) => {
        setTopArtistsLong(res.body.items);
      });
  }, [accessToken]);

  useEffect(() => {
    cancel.current = false;
    if (!search) return setSearchResults([]);
    if (!accessToken || !spotifyApi.current.getAccessToken()) return;
    if (debounceTimeoutRef.current) clearTimeout(debounceTimeoutRef.current);
    debounceTimeoutRef.current = setTimeout(() => {
      spotifyApi.current.searchTracks(search).then((res) => {
        if (cancel.current) return;
        setSearchResults(
          res.body.tracks.items.map((track) => {
            const smallestAlbumImage = track.album.images.reduce(
              (smallest, image) => {
                if (image.height < smallest.height) return image;
                return smallest;
              },
              track.album.images[0]
            );
            return {
              artist: track.artists[0].name,
              title: track.name,
              uri: track.uri,
              albumUrl: smallestAlbumImage.url, // Removed the trailing period here
            };
          })
        );
      });
    }, 300);
    return () => {
      if (debounceTimeoutRef.current) clearTimeout(debounceTimeoutRef.current);
      cancel.current = true;
    };
  }, [search, accessToken]);

  const handleSearch = (e) => {
    e.preventDefault();
  };

  useEffect(() => {
    if (!accessToken) return;
    spotifyApi.current.setAccessToken(accessToken);
    spotifyApi.current.getMyTopArtists().then((res) => {
      setTopArtists(res.body.items);

      const allGenres = res.body.items.flatMap((artist) => artist.genres);
      const genreCount = allGenres.reduce((acc, genre) => {
        acc[genre] = (acc[genre] || 0) + 1;
        return acc;
      }, {});
      const topGenres = Object.entries(genreCount)
        .sort((a, b) => b[1] - a[1])
        .slice(0, 5)
        .map(([genre]) => genre);
      setTopGenres(topGenres);
    });

    spotifyApi.current.getMyTopTracks().then((res) => {
      setTopTracks(res.body.items);
      console.log(res.body.items);

      spotifyApi.current
        .getMyTopTracks({ time_range: "short_term" })
        .then((res) => {
          setTopTracksShort(res.body.items);
        });
      spotifyApi.current
        .getMyTopTracks({ time_range: "medium_term" })
        .then((res) => {
          setTopTracksMedium(res.body.items);
        });
      spotifyApi.current
        .getMyTopTracks({ time_range: "long_term" })
        .then((res) => {
          setTopTracksLong(res.body.items);
        });
    });
  }, [accessToken]);

  useEffect(() => {
    if (!accessToken) return;
    spotifyApi.current.setAccessToken(accessToken);
    spotifyApi.current
      .getMyRecentlyPlayedTracks({ limit: 10 })
      .then((response) => {
        setRecentlyPlayed(response.body.items);
      });
  }, [accessToken]);

  return (
    <div className="dashboard">
      <Header />
      <section className="dashboard__hero">
        <button onClick={() => setSelectedTimeRange("short_term")}>
          Past 4 weeks
        </button>
        <button onClick={() => setSelectedTimeRange("medium_term")}>
          Past 6 months
        </button>
        <button onClick={() => setSelectedTimeRange("long_term")}>
          All time
        </button>

        {/* <form className="dashboard__form" onSubmit={handleSearch}> */}
        {/* <input
            className="dashboard__input"
            type="text"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            placeholder="Search..."
          /> */}
        {/* <button className="dashboard__button" type="submit">
            Search
          </button> */}
        {/* </form> */}
      </section>
      <div className="dashboard__songs"></div>
      {searchResults.map((track) => (
        <TrackSearchResult
          track={track}
          key={track.uri}
          chooseTrack={chooseTrack}
        />
      ))}

