import React, { useState } from "react";
import Header from "../Header/Header";
import Tracks from "../Tracks/Tracks";
import Artists from "../Artists/Artists";
import Genres from "../Genres/Genres";
import "./Dashboard.scss";
import SpotifyWebApi from "spotify-web-api-node";

function Dashboard({ accessToken }) {
  const [selectedTimeRange, setSelectedTimeRange] = useState("short_term");
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [searchResults, setSearchResults] = useState([]);

  return (
    <div className="dashboard">
      <Header onCategorySelect={setSelectedCategory} />
      {selectedCategory && selectedCategory !== "recently-played" && (
        <section className="dashboard__hero">
          <button
            className="dashboard__btn"
            onClick={() => setSelectedTimeRange("short_term")}
          >
            Past 4 weeks
          </button>
          <button
            className="dashboard__btn"
            onClick={() => setSelectedTimeRange("medium_term")}
          >
            Past 6 months
          </button>
          <button
            className="dashboard__btn"
            onClick={() => setSelectedTimeRange("long_term")}
          >
            All time
          </button>
        </section>
      )}
      {selectedCategory === 'tracks/top' && <Tracks accessToken={accessToken} selectedTimeRange={selectedTimeRange} />}
      {selectedCategory === 'artists/top' && <Artists accessToken={accessToken} selectedTimeRange={selectedTimeRange} />}
      {selectedCategory === 'genres/top' && <Genres accessToken={accessToken} selectedTimeRange={selectedTimeRange} />}
    </div>
  );
}

export default Dashboard;
