import React, { useEffect, useState, useRef } from 'react';
import SpotifyWebApi from 'spotify-web-api-node';

function SpotifyPlayer({ accessToken, trackId }) {
  const [sdkReady, setSdkReady] = useState(false);
  const [deviceId, setDeviceId] = useState(null);
  const spotifyApi = useRef(null);
  const player = useRef(null);

  useEffect(() => {
    // Load Spotify Web Playback SDK
    const scriptTag = document.createElement('script');
    scriptTag.src = 'https://sdk.scdn.co/spotify-player.js';
    scriptTag.async = true;
    scriptTag.onload = () => setSdkReady(true);
    document.body.appendChild(scriptTag);

    // Define the global function that the SDK calls when it's ready
    window.onSpotifyWebPlaybackSDKReady = () => {
      console.log('Spotify Web Playback SDK ready');
    };
  }, []);

  useEffect(() => {
    if (!accessToken || !sdkReady) return;

    spotifyApi.current = new SpotifyWebApi();
    spotifyApi.current.setAccessToken(accessToken);

    // Create a new player
    player.current = new window.Spotify.Player({
      name: 'Web Playback SDK Player',
      getOAuthToken: cb => { cb(accessToken); }
    });

    // Error handling
    player.current.addListener('initialization_error', ({ message }) => { console.error(message); });
    player.current.addListener('authentication_error', ({ message }) => { console.error(message); });
    player.current.addListener('account_error', ({ message }) => { console.error(message); });
    player.current.addListener('playback_error', ({ message }) => { console.error(message); });

    // Playback status updates
    player.current.addListener('player_state_changed', state => {
      console.log(state);
      setPlayerState(state);
    });

    // Ready
    player.current.addListener('ready', ({ device_id }) => {
      console.log('Ready with Device ID', device_id);
    });

    // Not Ready
    player.current.addListener('not_ready', ({ device_id }) => {
      console.log('Device ID has gone offline', device_id);
    });

    // Connect to the player
    player.current.connect();

    return () => {
      // Clean up on unmount
      player.current && player.current.disconnect();
    };
  }, [accessToken, sdkReady]);

  useEffect(() => {
    if (!player.current || !trackId || !deviceId) return; // Check if the device ID is available

    // Play the track using spotify-web-api-node
    spotifyApi.current.play({ uris: [`spotify:track:${trackId}`] })
      .then(function() {
        console.log('Playback started');
      }, function(err) {
        console.error('Something went wrong!', err);
      });
  }, [trackId, deviceId]); // Add deviceId as a dependency

  return null; // This component does not render anything
}

export default SpotifyPlayer;
