import React, { useEffect, useState } from 'react';
import useAuth from '../../useAuth';
import './Settings.scss';

function Settings({ accessToken, refreshToken, setAccessToken, setRefreshToken }) {
  const [isOpen, setIsOpen] = useState(false);

  const handleEmailOpt = (e) => {
    // handle email opt in/out
  };

  const handleSpotifyRedirect = () => {
    // redirect to user's Spotify page
    window.location.href = 'https://open.spotify.com';
  };

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  function logout() {
    // Clear the tokens from the application's state
    setAccessToken(null);
    setRefreshToken(null);

    // Clear the tokens from local storage
    localStorage.removeItem('accessToken');
    localStorage.removeItem('refreshToken');

    // Clear the tokens from cookies
    document.cookie = 'accessToken=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;';
    document.cookie = 'refreshToken=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;';

    // Redirect the user to the login page
    window.location = "/";

    useEffect(() => {
      console.log(refreshToken)
    }, [refreshToken]);
  }

  return (
    <div className="settings">
      <h2 className="settings__title" onClick={toggleDropdown}>
        Settings &#9662;
      </h2>
      {isOpen && (
        <ul className="settings__dropdown">
          <li onClick={handleEmailOpt}>Toggle Email Opt-In/Out</li>
          <li onClick={handleSpotifyRedirect}>Go to Spotify</li>
          <li onClick={logout}>Logout</li>
        </ul>
      )}
    </div>
  );
}

export default Settings;

