import React, { useState } from "react";


function Dashboard({ accessToken }) {
  const [selectedTimeRange, setSelectedTimeRange] = useState("short_term");
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [searchResults, setSearchResults] = useState([]);

  return (
    <div className="dashboard">
      <Header onCategorySelect={setSelectedCategory} />
      {selectedCategory && selectedCategory !== "recently-played" && (
        <section className="dashboard__hero">
          <button
            className="dashboard__btn"
            onClick={() => setSelectedTimeRange("short_term")}
          >
            Past 4 weeks
          </button>
          <button
            className="dashboard__btn"
            onClick={() => setSelectedTimeRange("medium_term")}
          >
            Past 6 months
          </button>
          <button
            className="dashboard__btn"
            onClick={() => setSelectedTimeRange("long_term")}
          >
            All time
          </button>
        </section>
      )}
      <Tracks accessToken={accessToken} selectedTimeRange={selectedTimeRange} />
      <Artists
        accessToken={accessToken}
        selectedTimeRange={selectedTimeRange}
      />
      <Genres accessToken={accessToken} selectedTimeRange={selectedTimeRange} />
      {searchResults.map((track) => (
        <TrackSearchResult
          track={track}
          key={track.uri}
          chooseTrack={chooseTrack}
        />
      ))}
    </div>
  );
}

export default Dashboard;
