import React, { useContext, useState } from "react";
import { useLocation } from "react-router-dom";
import AuthContext from "../../Auth/AuthContext";
import "./Dashboard.scss";
import Artists from "../Artists/Artists";
import Tracks from "../Tracks/Tracks";
import Genres from "../Genres/Genres";
import RecentlyPlayed from "../RecentlyPlayed/RecentlyPlayed";
import Header from "../Header/Header";
import Playlist from "../Playlist/Playlist";
import Mood from "../Mood/Mood";
import Settings from "../Settings/Settings";
import { Link } from "react-router-dom";
import HeaderLogo from "../../assets/images/sound-waves.png";
import ExpandArrow from "../../assets/images/Expand_Arrow.png";

import { Routes, Route } from "react-router-dom";

function Dashboard() {
  const { accessToken } = useContext(AuthContext);
  const [selectedTimeRange, setSelectedTimeRange] = useState("short_term");
  const location = useLocation();
  const [isDropdownVisible, setDropdownVisible] = useState(false);

  const toggleDropdown = () => {
    setDropdownVisible(!isDropdownVisible);
  };

  const handleStatsOptionClick = () => {
    setIsStatsOptionSelected(true);
    setSelectedTimeRange("short_term"); // Reset the time range when a new option is clicked
  };

  const [isStatsOptionSelected, setIsStatsOptionSelected] = useState(false);



  return (
    <div className="dashboard">
      <Header />

      <section className="dashboard__hero">
        <ul className="dashboard__nav">
          <div className="dashboard__nav--container" onClick={toggleDropdown}>
            <div className="dashboard__nav--container">
              Stats
              <img
                className="dashboard__nav--icon"
                src={ExpandArrow}
                alt="expand arrow"
              />
            </div>

            {isDropdownVisible && (
          <ul className="dashboard__dropdown">
            <Link to="/dashboard/tracks" onClick={() => handleStatsOptionClick('tracks')}>
              <li className="dashboard__dropdown--item">Top Tracks</li>
            </Link>
            <Link to="/dashboard/artists" onClick={() => handleStatsOptionClick('artists')}>
              <li className="dashboard__dropdown--item">Top Artists</li>
            </Link>
            <Link to="/dashboard/genres" onClick={() => handleStatsOptionClick('genres')}>
              <li className="dashboard__dropdown--item">Top Genres</li>
            </Link>
                <Link to="/dashboard/recent">
                  <li className="dashboard__dropdown--item">Recently Played</li>
                </Link>
              </ul>
            )}
          </div>
          <Link to="/dashboard/mood">
            <li className="dashboard__nav--list">Your Mood</li>
          </Link>
          <Link to="/dashboard/playlist">
            <li className="dashboard__nav--list">Create a Playlist</li>
          </Link>
        </ul>
        </section>

        {
  ['/dashboard/tracks', '/dashboard/artists', '/dashboard/genres'].includes(location.pathname) && (
    <div className="dashboard__btn-wrapper">
    <button
      className={`dashboard__selected-btn ${selectedTimeRange === "short_term" ? "active" : ""}`}
      onClick={() => setSelectedTimeRange("short_term")}
    >
      Past 4 weeks
    </button>
    <button
      className={`dashboard__selected-btn ${selectedTimeRange === "medium_term" ? "active" : ""}`}
      onClick={() => setSelectedTimeRange("medium_term")}
    >
      Past 6 months
    </button>
    <button
      className={`dashboard__selected-btn ${selectedTimeRange === "long_term" ? "active" : ""}`}
      onClick={() => setSelectedTimeRange("long_term")}
    >
      All time
    </button>
  </div>
  )
}


      <div className="dashboard__pages">
        <Routes>
          <Route
            path="artists"
            element={
              <Artists
                selectedTimeRange={selectedTimeRange}
                setSelectedTimeRange={setSelectedTimeRange}
                accessToken={accessToken}
              />
            }
          />
          <Route
            path="tracks"
            element={
              <Tracks
                selectedTimeRange={selectedTimeRange}
                setSelectedTimeRange={setSelectedTimeRange}
                accessToken={accessToken}
              />
            }
          />
          <Route
            path="genres"
            element={
              <Genres
                selectedTimeRange={selectedTimeRange}
                setSelectedTimeRange={setSelectedTimeRange}
                accessToken={accessToken}
              />
            }
          />
          <Route
            path="recent"
            element={<RecentlyPlayed accessToken={accessToken} />}
          />
          <Route
            path="playlist"
            element={<Playlist accessToken={accessToken} />}
          />
          <Route path="mood" element={<Mood accessToken={accessToken} />} />
        </Routes>
      </div>
    </div>
  );
}

export default Dashboard;
