import React, { useEffect, useRef } from "react";
import SpotifyWebApi from "spotify-web-api-node";
import { Chart } from "react-google-charts";

function Mood({ accessToken }) {
  const spotifyApi = useRef(
    new SpotifyWebApi({
      clientId: "6ba0cc8b29e145ea99f2401c09a35e6e",
    })
  );

  const [moodData, setMoodData] = React.useState([]);

  useEffect(() => {
    if (!accessToken) return;
    spotifyApi.current.setAccessToken(accessToken);
    spotifyApi.current
      .getMyRecentlyPlayedTracks({ limit: 50 })
      .then((response) => {
        const trackIds = response.body.items.map(item => item.track.id);
        return spotifyApi.current.getAudioFeaturesForTracks(trackIds);
      })
      .then((response) => {
        const audioFeatures = response.body.audio_features;

        const data = audioFeatures.map((features, index) => {
          const date = new Date(new Date().getTime() - index * 1000 * 60 * 60 * 24); // Subtract index days from current date
          const dateString = date.toLocaleDateString('en-US', { month: 'short', day: '2-digit' }); // Convert date to 'MMM dd' format
          return [dateString, features.valence];
        });

        console.log(audioFeatures);

        setMoodData([['Time', 'Mood (valence)'], ...data]);
      })
      .catch((err) => {
        console.error("Something went wrong:", err);
      });
  }, [accessToken]);

  return (
    <div style={{ position: 'relative' }}>
      <div style={{ 
        position: 'absolute', 
        left: '10px', 
        top: '50%', 
        transform: 'translateY(-50%) rotate(-90deg)', 
        color: '#FFFFFF',
        fontFamily: 'Gotham',
        fontSize: '13px'
      }}>
        Mood (valence)
      </div>
      <Chart
  width={'500px'}
  height={'300px'}
  chartType="LineChart"
  loader={<div>Loading Chart</div>}
  data={moodData}
  options={{
    backgroundColor: '#191414',
    fontName: 'Gotham',
    fontSize: 13,
    hAxis: {
      title: 'Time',
      format: 'MMM dd', // Changed format to display month and day without year
      titleTextStyle: { color: '#FFFFFF' },
      textStyle: { color: '#FFFFFF' },
      gridlines: { color: 'transparent' }, // Hide gridlines
    },
    vAxis: {
      title: '', // Remove original title
      titleTextStyle: { color: '#FFFFFF' },
      textStyle: { color: '#FFFFFF' },
      gridlines: { color: 'transparent' }, // Hide gridlines
    },
    legend: { textStyle: { color: '#FFFFFF' } },
    colors: ['#a52714', '#097138'], // Change line colors here
  }}
/>
    </div>
  );
}

export default Mood;
