import React, { useState, useContext } from 'react';
import './Settings.scss';
import AuthContext from '../Contexts/AuthContext';

function Settings() {
  const [isOpen, setIsOpen] = useState(false);
  const { accessToken, setAccessToken } = useContext(AuthContext);

  const logout = () => {
    // Clear the access token from context
    // If you're storing the access token in local storage, clear it from there too
    localStorage.removeItem('accessToken');
    // Redirect the user to the login page

  };

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="settings">
      <h2 className="settings__title" onClick={toggleDropdown}>
        Settings &#9662;
      </h2>
      {isOpen && (
        <ul className="settings__dropdown">
          {/* <li onClick={handleEmailOpt}>Toggle Email Opt-In/Out</li>
          <li onClick={handleSpotifyRedirect}>Go to Spotify</li> */}
          <li onClick={logout}>Logout</li>
        </ul>
      )}
    </div>
  );
}

export default Settings;
