import "./Header.scss";
import Settings from "../Settings/Settings";
import { Link, useNavigate } from 'react-router-dom';
import HeaderLogo from "../../assets/images/sound-waves.png";
import { useState } from "react";

function Header({ onCategorySelect }) {
  const [accessToken, setAccessToken] = useState(localStorage.getItem('accessToken'));
  const [refreshToken, setRefreshToken] = useState(localStorage.getItem('refreshToken'));
  const navigate = useNavigate();

  const handleCategorySelect = (category) => {
    onCategorySelect(category);
    navigate(`/dashboard/${category}`);
  };

  return (
    <div className="header">
      <div className="header__container">
        <img src={HeaderLogo} alt="logo" />
        <h1>Soundtrack Analyzer</h1>

        <ul className="header__nav">
          <li className="header__nav--list" onClick={() => handleCategorySelect('tracks/top')}>Top Tracks</li>
          <li className="header__nav--list" onClick={() => handleCategorySelect('artists/top')}>Top Artists</li>
          <li className="header__nav--list" onClick={() => handleCategorySelect('genres/top')}>Top Genres</li>
          <li className="header__nav--list" onClick={() => handleCategorySelect('recent')}>Recently Played</li>
          <li className="header__nav--list">Your Mood</li>
        </ul>
      </div>
      <div className="header__settings">
        <Settings accessToken={accessToken} setAccessToken={setAccessToken} refreshToken={refreshToken} setRefreshToken={setRefreshToken} />
      </div>
    </div>
  );
}

export default Header;
